import React, { useEffect, useState } from 'react';
import { getOne } from '../../api/todoApi';
import ResultModal from '../common/ResultModal';
import useCustomMove from '../../hooks/useCustomMove';

const initState = {
    tno:0,
    title:'',
    content:'',
    dueDate:'',
    complet:false
}
function ModifyComponent({tno}) {

    const [todo, setTodo] = useState(initState)
    const [result, setResult] = useState(null)
    const {moveToList, moveToRead} = useCustomMove()
    useEffect(() => {
        getOne(tno).then(data=>{
            console.log(data)
            setTodo(data)
        })
    },[tno])

    const handleChangeTodo = (e) => {
        //todo[title]...todo[content].. 
        console.log(e.target.nmae, e.target.value)
        todo[e.target.name] = e.target.value

        setTodo({...todo})
    }

    const handleChangeTodoComplete  = (e) => {
        const value = e.target.value
        todo.complete = (value === 'Y')

        setTodo({...todo})
    }

    const handleClickModify = () => { //버튼 클릭시 

        putOne(todo).then(data => {
          console.log("modify result: " + data)
          setResult({result:'Modified'})
        })
      }

      const handleClickDelete = () => { //버튼 클릭시 

        deleteOne(tno).then( data => {
          console.log("delete result: " + data)
          setResult({result:'Deleted'})
        })

      }

    return (
        <div className = "border-2 border-sky-200 mt-10 m-2 p-4"> 

        {result ? <ResultModal title={'처리결과'} content={result.result} callbackFn={closeModal}></ResultModal>  :<></>}

        <div className="flex justify-center mt-10">
          <div className="relative mb-4 flex w-full flex-wrap items-stretch">
            <div className="w-1/5 p-6 text-right font-bold">TNO</div>
            <div className="w-4/5 p-6 rounded-r border border-solid shadow-md bg-gray-100">
              {todo.tno}        
            </div>  
          </div>
        </div>
        <div className="flex justify-center">
          <div className="relative mb-4 flex w-full flex-wrap items-stretch">
            <div className="w-1/5 p-6 text-right font-bold">WRITER</div>
            <div className="w-4/5 p-6 rounded-r border border-solid shadow-md bg-gray-100">
              {todo.writer}        
            </div>

          </div>
        </div>
        <div className="flex justify-center">
          <div className="relative mb-4 flex w-full flex-wrap items-stretch">
            <div className="w-1/5 p-6 text-right font-bold">TITLE</div>
            <input className="w-4/5 p-6 rounded-r border border-solid border-neutral-300 shadow-md" 
             name="title"
             type={'text'} 
             value={todo.title}
             onChange={handleChangeTodo}
             >
             </input>
          </div>  
        </div>
        <div className="flex justify-center">
          <div className="relative mb-4 flex w-full flex-wrap items-stretch">
            <div className="w-1/5 p-6 text-right font-bold">DUEDATE</div>
            <input className="w-4/5 p-6 rounded-r border border-solid border-neutral-300 shadow-md" 
             name="dueDate"
             type={'date'} 
             value={todo.dueDate}
             onChange={handleChangeTodo}
             >
             </input>
          </div>
        </div>
        <div className="flex justify-center">
          <div className="relative mb-4 flex w-full flex-wrap items-stretch">
            <div className="w-1/5 p-6 text-right font-bold">COMPLETE</div>
            <select
              name="status" 
              className="border-solid border-2 rounded m-1 p-2"
              onChange={handleChangeTodoComplete} 
              value = {todo.complete? 'Y':'N'} >
              <option value='Y'>Completed</option>
              <option value='N'>Not Yet</option>
            </select>
          </div>
        </div>

        <div className="flex justify-end p-4">
          <button type="button" 
            className="inline-block rounded p-4 m-2 text-xl w-32 text-white bg-red-500"
            onClick={handleClickDelete}
          >
            Delete
          </button>
          <button type="button" 
            className="rounded p-4 m-2 text-xl w-32 text-white bg-blue-500"
            onClick={handleClickModify}
          >
            Modify
          </button>  
        </div>
        </div>
    );
}

export default ModifyComponent;
